#客户端为UTF8
SET NAMES UTF8;
#丢弃数据库
DROP DATABASE IF EXISTS jianshu;
#创建数据库
CREATE DATABASE jianshu CHARSET=UTF8;
#进入数据库
USE jianshu;

#主页的内容
CREATE TABLE js(
	id INT PRIMARY KEY AUTO_INCREMENT,  
	title VARCHAR(50),                   
	content VARCHAR(50),                    
	author    VARCHAR(24),
	img  VARCHAR(50)
);
#更多内容

CREATE TABLE special(
	id INT PRIMARY KEY AUTO_INCREMENT, 
	img	varchar(50),
	titles varchar(10),
	contents varchar(50),
	attention varchar(30)
);
CREATE TABLE recommend(
	id INT PRIMARY KEY AUTO_INCREMENT, 
	img varchar(50),
	name varchar(15),
	title varchar(50),
	content1  varchar(100),
	content2  varchar(100)
);
CREATE TABLE `jianshu_comment` (
  `id` int(11) NOT NULL,
  `img` varchar(128) NOT NULL,
  `content` varchar(140) DEFAULT NULL,
  `ctime` varchar(140) DEFAULT NULL,
  `user` varchar(50) DEFAULT NULL,
  `uid` int(11) DEFAULT NULL
);

CREATE TABLE `details` (
  `uid` int(11) NOT NULL,
  `upwd` varchar(32) DEFAULT NULL,
  `email` varchar(64) DEFAULT NULL,
  `phone` varchar(16) DEFAULT NULL,
  `uname` varchar(32) DEFAULT NULL,
  `gender` int(11) DEFAULT NULL,
  `dates` varchar(128) DEFAULT NULL,
  `title` varchar(128) DEFAULT NULL,
  `mainImg` mediumtext NOT NULL,
  `type` varchar(128) NOT NULL,
  `authorImg` varchar(65) NOT NULL,
  `author` varchar(128) NOT NULL,
  `counts` text NOT NULL,
  `intro` text NOT NULL,
  `content1` text,
  `content2` text NOT NULL,
  `content3` text NOT NULL,
  `content4` text NOT NULL,
  `content5` text NOT NULL,
  `content6` text NOT NULL,
  `authorInfo` text NOT NULL,
  `img1` varchar(128) NOT NULL,
  `img2` varchar(128) NOT NULL,
  `img3` varchar(128) NOT NULL,
  `img4` varchar(128) NOT NULL,
  `img5` varchar(128) NOT NULL,
  `img6` mediumtext NOT NULL
);




